UPDATE `wp_series_volume` AS `sv`, (SELECT `series_volume_id`, COUNT(*) AS `count` FROM `wp_book_publish` GROUP BY `series_volume_id`) AS `tmp` SET `book_num` = `tmp`.`count` WHERE `sv`.`id` = `tmp`.`series_volume_id`;
UPDATE `wp_series` AS `sv`, (SELECT `series_code`, COUNT(*) AS `count` FROM `wp_book_publish` GROUP BY `series_code`) AS `tmp` SET `book_num` = `tmp`.`count` WHERE `sv`.`code` = `tmp`.`series_code`;
